    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        float num1 = Float.parseFloat(jTextField1.getText()); //get the text from textfield and converts to float
        float num2 = Float.parseFloat(jTextField2.getText());  //get the text from textfield and converts to float
        try {
        jTextField3.setText(num1%num2+""); //gives remainder if no error is there
        }
        catch(ArithmeticException e)
        { 
        jTextField3.setText("Cannot divide"); //set the error message if error comes.
        } 
    }//GEN-LAST:event_jButton6ActionPerformed

