    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        int num1 = Integer.parseInt(jTextField1.getText()); //get the text from textfield and converts to int
        int num2 = Integer.parseInt(jTextField2.getText());//get the text from textfield and converts to int
        jTextField3.setText(num1+num2+"");//sets the addition of the two numbers to the result text field
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton8ActionPerformed
        int num1 = Integer.parseInt(jTextField1.getText());//get the text from textfield and converts to int
        int num2 = Integer.parseInt(jTextField2.getText());//get the text from textfield and converts to int
        jTextField3.setText(num1-num2+""); //sets the subtraction of the two numbers to the result text field
    }//GEN-LAST:event_jButton8ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        float num1 = Float.parseFloat(jTextField1.getText());//get the text from textfield and converts to float
        float num2 = Float.parseFloat(jTextField2.getText());//get the text from textfield and converts to float
        try{
        jTextField3.setText(num1/num2+""); //division is performed and result is set if no error comes.
        }
        catch(ArithmeticException e)
        {
            jTextField3.setText("Cannot Divide"); //if error comes then sets the error message in the text field
        }
        
        
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton9ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton9ActionPerformed
        int num1 = Integer.parseInt(jTextField1.getText());//get the text from textfield and converts to int
        int num2 = Integer.parseInt(jTextField2.getText());//get the text from textfield and converts to int
        jTextField3.setText(num1*num2+"");//sets the resutlt of multiplication
    }//GEN-LAST:event_jButton9ActionPerformed

